//Determine the app is it pro without ads or not
jsToJava.ispro = false
jsToJava.itspro = function(n){
    if(n == 1){
        jsToJava.hideBanner()
        jsToJava.ispro = true
    }else{
        jsToJava.ispro = false
    }
}